mapper.select:
SELECT *
  FROM tb_menu a right join tb_menu_grt b on a.no_menu = b.no_menu
  WHERE b.no_user = #{no_user, jdbcType=VARCHAR}

		AND a.menu_level =  #{menu_level, jdbcType=VARCHAR}

	ORDER BY ${order_column} ${order_type}

SELECT count(*)
FROM tb_menu a right join tb_menu_grt b on a.no_menu = b.no_menu
WHERE b.no_user = #{no_user, jdbcType=VARCHAR}

	AND a.menu_level =  #{menu_level, jdbcType=VARCHAR}
