mapper.select:
	SELECT *
	  FROM tb_com_code
	 WHERE CODE_GROUP = #{code_group, jdbcType=VARCHAR}
	   AND USE_YN = #{use_yn, jdbcType=VARCHAR}
     ORDER BY CODE_SNO ASC

	SELECT code_name
	  FROM tb_com_code
	 WHERE CODE_GROUP = #{code_group, jdbcType=VARCHAR}
	   AND CODE_VALUE = #{code_value, jdbcType=VARCHAR}
     ORDER BY CODE_SNO ASC

select ${column_name} as column_name
, ${column_pk} as column_pk
from ${table_name}
where del_yn = #{del_yn, jdbcType=VARCHAR}
